// eslint-disable-next-line no-use-before-define
import * as React from 'react'
import { Animated, StyleSheet, Easing } from 'react-native'

interface LoadingViewState {
  rotateValue: Animated.Value,
}

// eslint-disable-next-line @typescript-eslint/no-empty-interface
interface LoadingViewProps { }

const styles = StyleSheet.create({
  toastLoading: {
    width: 25,
    height: 25
  }
})

class LoadingView extends React.Component<LoadingViewProps, LoadingViewState> {
  constructor (props: LoadingViewProps) {
    super(props)
    this.state = {
      rotateValue: new Animated.Value(0) // 初始值
    }
  }

  componentDidMount (): void {
    this.startAnimation()
  }

  startAnimation (): void {
    this.state.rotateValue.setValue(0)
    Animated.timing(this.state.rotateValue, {
      toValue: 1,
      duration: 2000,
      easing: Easing.linear,
      useNativeDriver: true
    }).start(() => this.startAnimation())
  }

  // eslint-disable-next-line @typescript-eslint/explicit-module-boundary-types
  render () {
    return (
      <Animated.Image
        source={{ uri: 'data:image/png;base64,' }}
        style={[
          styles.toastLoading, {
            transform: [
              {
                rotateZ: this.state.rotateValue.interpolate({
                  inputRange: [0, 1],
                  outputRange: ['0deg', '360deg']
                })
              }]
          }]}
      />
    )
  }
}

export default LoadingView
